import React, { Component } from 'react'
import './ProductDetsInfo.css'

export default class ProductDetsInfo extends Component {
  constructor(props){
    super(props)
    this.state = {
      showMoreInfo: false
    }
    this.handleShowMoreBtnClick = this.handleShowMoreBtnClick.bind(this);
  }

  handleShowMoreBtnClick(){
    this.setState(state => ({
      showMoreInfo: !state.showMoreInfo
    }))
  }

  render() {
    const { title, price, rating, color, size, description } = this.props;
    return (
      <div className="product-dets-info">
        <h3 className="product-dets-info__name">
          {title}
        </h3>
        <p className="product-dets-info__price">
          {price}$
        </p>
        <p className="product-dets-info__rating">
          <span>Rating:</span> {rating}
        </p>
        <p className="product-dets-info__color">
          <span>Color:</span> {color.join(', ')}
        </p>
        <p className="product-dets-info__size">
          <span>Size:</span> {size.join(', ')}
        </p>
        <div className="product-dets-info-desc">
          <h4 className="product-dets-info-desc__heading">
            Description
          </h4>
          <p className="product-dets-info-desc__description">
            {description}
          </p>
          <div className="product-dets-info-desc-mask">
            <button 
              className="product-dets-info-desc-mask__btn"
              onClick={this.handleShowMoreBtnClick}
            >SHOW MORE</button>
          </div>
        </div>
      </div>
    )
  }
}


const descDescriptionStyle = {
  overflow: this.state.showMoreInfo ? 'auto' : 'hidden',
  height: this.state.showMoreInfo ? 'auto' : '100px'
}

const descMaskStyle = {
  position: this.state.showMoreInfo ? 'relative' : 'absolute',
  top: this.state.showMoreInfo ? '0' : '100px'
}

let show = false;
const getMaskBtnStyle = () => {
  if (show === false) {
    show = !show;
    document.querySelector('.product-dets-info-desc__description').style = 'overflow: auto; height: auto';
    document.querySelector('.product-dets-info-desc-mask').style = 'position: relative; top: 0;';
    document.querySelector('.product-dets-info-desc-mask__btn').innerHTML = 'HIDE';
  } else if (show){
    show = !show;
    document.querySelector('.product-dets-info-desc__description').style = 'overflow: hidden; height: 100px';
    document.querySelector('.product-dets-info-desc-mask').style = 'position: absolute; top: 100px;';
    document.querySelector('.product-dets-info-desc-mask__btn').innerHTML = 'SHOW MORE';
  }
}
