import React from 'react'
import './Filter.css' 
import FilterItem from '../FilterItem/FilterItem'

export default function Filter
({ 
  filterName, 
  products, 
  filterItems, 
  currentFilterTagValue,
  currentFilterTagName, 
  onFilterTagClick,
  showFilterOptions 
}) { 


  let filters = null;
  if ( filterName !== 'Sort By' ) {
    filters = ['All']
    products.forEach(product => { 
      product[filterItems].forEach(filterItem => {
        if (filters.indexOf(filterItem) !== -1) {
          return;
        }
        filters.push(filterItem)
      })
    })
  } else {
    filters = ['Our Picks', 'Price: low to high', 'Price: high tolow'];
  }
  return (
    <div className="filterbar-somefilter">
      <button 
        className="filterbar-somefilter-name">{filterName}</button>
      {showFilterOptions && <div className="filterbar-somefilter-options">
        {filters.map(filter => (
          <FilterItem 
            key={filter} 
            tagFilter={filter}
            filterName={filterName}
            currentFilterTagValue={currentFilterTagValue} 
            currentFilterTagName={currentFilterTagName} 
            onFilterTagClick={onFilterTagClick} 
          />
        ))}
      </div>}
    </div>
  )
}
